import React, { useEffect, useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";
import styles from "./css/mainPage.module.css";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/autoplay";
import "swiper/css/navigation";

import "./css/mainPage.module.css";

// import required modules
import {
  Pagination,
  Navigation,
  Autoplay,
  FreeMode,
  Scrollbar,
  Virtual,
} from "swiper/modules";

export default function MainPage() {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [selectedButton, setSelectedButton] = useState("button01"); // 상태 추가
  const [priceText, setPriceText] = useState("30,000원"); // 상태 추가
  const [vaccineText, setVaccineText] = useState("DTP 예방백신 1,200회");

  useEffect(() => {
    const handleResize = () => {
      setWindowWidth(window.innerWidth);
    };

    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const calculateFontSize2418 = () => {
    const minFontSize = 18;
    const maxFontSize = 24;

    // 1120에서 990 사이의 화면 너비 범위에서만 폰트 크기를 조정합니다.
    const fontSize =
      minFontSize +
      ((maxFontSize - minFontSize) *
        (Math.min(1120, Math.max(windowWidth, 990)) - 990)) /
        (1120 - 990);

    // 폰트 크기를 반환합니다.
    return `${fontSize}px`;
  };

  const calculateFontSize1612 = () => {
    const minFontSize = 12;
    const maxFontSize = 16;

    // 1120에서 640 사이의 화면 너비 범위에서만 폰트 크기를 조정합니다.
    const fontSize =
      minFontSize +
      ((maxFontSize - minFontSize) *
        (Math.min(1120, Math.max(windowWidth, 640)) - 640)) /
        (1120 - 640);

    // 폰트 크기를 반환합니다.
    return `${fontSize}px`;
  };
  const calculateFontSize1614 = () => {
    const minFontSize = 14;
    const maxFontSize = 16;

    // 1120에서 640 사이의 화면 너비 범위에서만 폰트 크기를 조정합니다.
    const fontSize =
      minFontSize +
      ((maxFontSize - minFontSize) *
        (Math.min(1120, Math.max(windowWidth, 640)) - 640)) /
        (1120 - 640);

    // 폰트 크기를 반환합니다.
    return `${fontSize}px`;
  };
  const calculateFontSize2016 = () => {
    const minFontSize = 16;
    const maxFontSize = 20;

    // 1120에서 640 사이의 화면 너비 범위에서만 폰트 크기를 조정합니다.
    const fontSize =
      minFontSize +
      ((maxFontSize - minFontSize) *
        (Math.min(1120, Math.max(windowWidth, 640)) - 640)) /
        (1120 - 640);

    // 폰트 크기를 반환합니다.
    return `${fontSize}px`;
  };
  const calculateFontSize3220 = () => {
    const minFontSize = 20;
    const maxFontSize = 32;

    // 1120에서 640 사이의 화면 너비 범위에서만 폰트 크기를 조정합니다.
    const fontSize =
      minFontSize +
      ((maxFontSize - minFontSize) *
        (Math.min(1120, Math.max(windowWidth, 640)) - 640)) /
        (1120 - 640);

    // 폰트 크기를 반환합니다.
    return `${fontSize}px`;
  };
  const calculateFontSize3217 = () => {
    const minFontSize = 17;
    const maxFontSize = 32;

    // 1120에서 640 사이의 화면 너비 범위에서만 폰트 크기를 조정합니다.
    const fontSize =
      minFontSize +
      ((maxFontSize - minFontSize) *
        (Math.min(1120, Math.max(windowWidth, 640)) - 640)) /
        (1120 - 640);

    // 폰트 크기를 반환합니다.
    return `${fontSize}px`;
  };
  const calculateFontSize4826 = () => {
    const minFontSize = 26;
    const maxFontSize = 48;

    // 1120에서 640 사이의 화면 너비 범위에서만 폰트 크기를 조정합니다.
    const fontSize =
      minFontSize +
      ((maxFontSize - minFontSize) *
        (Math.min(1120, Math.max(windowWidth, 640)) - 640)) /
        (1120 - 640);

    // 폰트 크기를 반환합니다.
    return `${fontSize}px`;
  };

  //후원하기 금액 및 텍스트 교체
  // const priceSpan = document.getElementById("price");
  // const vaccinesSpan = document.getElementById("vaccines");
  const buttons = document.querySelectorAll("button");

  // 모든 버튼의 색상을 초기화
  buttons.forEach((button) => {
    button.classList.remove("active");
  });

  // 텍스트 변경 하나의 아이템 고정
  /*   const handleButtonClick = (buttonNumber) => {
    // setSelectedButton(buttonNumber);
    setSelectedButton(`button0${buttonNumber}`);

    if (buttonNumber === 1) {
      setPriceText("30,000원");
      setVaccineText("결핵 예방백신 1,200회");
    } else if (buttonNumber === 2) {
      setPriceText("50,000원");
      setVaccineText("농구공 120개");
    } else if (buttonNumber === 3) {
      setPriceText("100,000원");
      setVaccineText("콜레라 진단 키트 400개");
    }
  }; */

  //텍스트변경 두번째 복수 아이템 랜덤
  const handleButtonClick = (buttonNumber) => {
    setSelectedButton(buttonNumber);

    const prices = ["30,000원", "50,000원", "100,000원"];
    const vaccineOptions = [
      [
        "영양상태 측정 테이프 12,000개",
        "태양광램프 5개",
        "고영양비스킷 2,880개",
        "다회용 면 생리대 48세트",
        "영양실조치료우유 144개",
      ],
      [
        "농구공 120개",
        "비누 1,200개",
        "뇌수막염치료제/항생제 545병",
        "콜레라 진단 키트 200개",
        "다회용 면 생리대 80세트",
      ],
      [
        "책가방 300개",
        "말라리아 진단 키트 500개",
        "고영양비스킷 9,600개",
        "공책 800권",
        "DTP 예방백신 4,000회",
      ],
    ];

    const randomIndex = Math.floor(
      Math.random() * vaccineOptions[buttonNumber - 1].length
    );
    const randomVaccine = vaccineOptions[buttonNumber - 1][randomIndex];

    setPriceText(prices[buttonNumber - 1]); // 버튼 번호에 따라 가격 설정
    setVaccineText(randomVaccine); // 랜덤하게 백신 정보 설정
  };

  return (
    <>
      <div className={styles.mainCover01wrap}>
        <Swiper
          pagination={true}
          autoplay={{
            delay: 3000,
            disableOnInteraction: true,
          }}
          loop={true}
          navigation={true}
          modules={[Pagination, Navigation, Autoplay]}
          className={styles.mainCover01}
        >
          {/* <SwiperSlide className={`${styles.mainCoverItem}${styles.cover01}`}> */}
          <SwiperSlide className={`${styles.mainCoverItem} ${styles.cover01}`}>
            <div>
              <h2>하나일 때 더 빛나는 유니세프 팀 반지</h2>
              <p>유니세프 팀 반지로 당신의 선한 영향력을 보여주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide className={`${styles.mainCoverItem} ${styles.cover02}`}>
            <div>
              <h2>모든 어린이와 함께하는 유니세프 팀 팔찌</h2>
              <p>가족과 함께 어린이들 이야기에 귀 기울여주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide className={`${styles.mainCoverItem} ${styles.cover03}`}>
            <div>
              <h2>어린이의 생명을 살리는 '출발점'</h2>
              <p>굶주린 어린이를 위해 출발점을 만들어주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide className={`${styles.mainCoverItem} ${styles.cover04}`}>
            <div>
              <h2>가자지구 긴급구호</h2>
              <p>참혹한 분쟁으로 위험에 처한 어린이를 구해주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
        </Swiper>
      </div>

      <div className={styles.mainCover02wrap}>
        <Swiper
          pagination={true}
          autoplay={{
            delay: 3000,
            disableOnInteraction: true,
          }}
          loop={false}
          navigation={false}
          modules={[Pagination, Autoplay]}
          className={styles.mainCover02}
        >
          <SwiperSlide
            className={`${styles.mainCoverItem}${styles.thumCover01}`}
          >
            <div>
              <h2>하나일 때 더 빛나는 유니세프 팀 반지</h2>
              <p>유니세프 팀 반지로 당신의 선한 영향력을 보여주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide
            className={`${styles.mainCoverItem}${styles.thumCover02}`}
          >
            {" "}
            <div>
              <h2>모든 어린이와 함께하는 유니세프 팀 팔찌</h2>
              <p>가족과 함께 어린이들 이야기에 귀 기울여주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide
            className={`${styles.mainCoverItem}${styles.thumCover03}`}
          >
            {" "}
            <div>
              <h2>어린이의 생명을 살리는 '출발점'</h2>
              <p>굶주린 어린이를 위해 출발점을 만들어주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide
            className={`${styles.mainCoverItem}${styles.thumCover04}`}
          >
            {" "}
            <div>
              <h2>가자지구 긴급구호</h2>
              <p>참혹한 분쟁으로 위험에 처한 어린이를 구해주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
        </Swiper>
      </div>

      <div className={styles.mainSponsor}>
        <div className={styles.mainSponsorSelect}>
          <div className={styles.selectMoney}>
            <div className={styles.selectBox}>
              <select name="" id="">
                <option value="">정기</option>
                <option value="">일시</option>
              </select>
            </div>
            <div className={styles.sponsorCenterbox}>
              <ul>
                <li>
                  <button
                    id="button01"
                    onClick={() => handleButtonClick(1)} // 버튼 클릭 핸들러 연결
                    className={selectedButton === "button01" ? "active" : ""}
                  >
                    30,000원
                  </button>
                </li>
                <li>
                  <button
                    id="button02"
                    onClick={() => handleButtonClick(2)} // 버튼 클릭 핸들러 연결
                    className={selectedButton === "button02" ? "active" : ""}
                  >
                    50,000원
                  </button>
                </li>
                <li>
                  <button
                    id="button03"
                    onClick={() => handleButtonClick(3)} // 버튼 클릭 핸들러 연결
                    className={selectedButton === "button03" ? "active" : ""}
                  >
                    100,000원
                  </button>
                </li>
                <li>
                  <input type="text" placeholder="금액직접입력" />
                </li>
              </ul>
              <div className={styles.sponsorBodyText}>
                <strong>
                  월 <span id="price">{priceText}</span>이면, 전 세계 어린이를
                  위해
                  <span id="vaccines"> {vaccineText}</span>를 지원할 수
                  있습니다.
                </strong>
              </div>
            </div>
          </div>
          <div className={styles.sponsorButton}>
            <strong>후원하기</strong>
          </div>
        </div>
      </div>

      <div className={styles.mainBodyUN}>
        <div className={styles.mainBodyUNHead}>
          <h2 style={{ fontSize: calculateFontSize4826() }}>
            <span>UN공식</span>기관
            <br />
            유니세프
          </h2>
          <p>
            유엔아동권리협약에 유일하게 명시된 아동권리 증진기구 유니세프,
            <br />
            후원자님의 소중한 후원금으로 전 세계 어린이 및 한국 어린이를
            돕습니다.
          </p>
        </div>
        <div className={styles.mainBodyUNThum}>
          <div>
            <div className={styles.imageWrap}>
              <img src="/main/un01.png " alt="" className={styles.w130120} />
            </div>
            <strong>
              <span>UN 공식 기관</span>으로
              <span>약 80년의 구호 활동 경험과 전문성</span>
            </strong>
          </div>
          <div>
            {/* <div className="imageWrap w170"> */}
            <div className={`${styles.imageWrap} ${styles.w170}`}>
              <img src="/main/un02.png " alt="" />
            </div>
            <strong>
              <span>유엔아동권리협약에 직접 명시</span>된 유일한 아동권리증진
              기관
            </strong>
          </div>
          <div>
            <div className={styles.imageWrap}>
              <img src="/main/un03.png " alt="" className={styles.w120120} />
            </div>
            <strong>
              유니세프 본부 및 외부 회계 법인 감사로
              <span>후원금 사용의 투명성 검증</span>
            </strong>
          </div>
          <div>
            <div className={styles.imageWrap}>
              <img src="/main/un04.jpg" alt="" className={styles.w120120} />
            </div>
            <strong>
              2023 대한민국 국가사회산업공헌 대상 비영리 기관
              <span> '세계 어린이 구호' 부문 3년 연속 수상</span>
            </strong>
          </div>
          <div>
            <div className={styles.imageWrap}>
              <img src="/main/un05.png" alt="" className={styles.w120120} />
            </div>
            <strong>
              2022년 원조투명성지수 평가 에서
              <span>2회 연속 최상위 등급 (Very Good) 획득</span>
            </strong>
          </div>
        </div>
        <div className={styles.mainBodyUNLink}>
          <div className={styles.unLinkWrap}>
            <div>
              <p style={{ fontSize: calculateFontSize1612() }}>
                유니세프 팩트체크
              </p>
              <strong style={{ fontSize: calculateFontSize3217() }}>
                유니세프, 믿을 수 있나요?
              </strong>
            </div>
            <img src="/main/unbtnimg01.png" alt="" />
          </div>
          <div className={styles.unLinkWrap}>
            <div>
              <p style={{ fontSize: calculateFontSize1612() }}>
                후원금 사용 내역
              </p>
              <strong style={{ fontSize: calculateFontSize3217() }}>
                후원금은 어떻게 쓰이나요?
              </strong>
            </div>
            <img src="/main/unbtnimg02.png" alt="" />
          </div>
        </div>
      </div>

      <div className={styles.mainChant}>
        <h2 style={{ fontSize: calculateFontSize4826() }}>
          유니세프 구호 현장
        </h2>
        <Swiper
          slidesPerView={1}
          pagination={false}
          loop={true}
          navigation={true}
          modules={[Pagination, Navigation, Autoplay]}
          spaceBetween={1000}
        >
          <SwiperSlide
            className={`${styles.mainChantSwiper} ${styles.chantSlide03}`}
          >
            <div className={styles.mainChantSwiperTitle}>
              <h3 style={{ fontSize: calculateFontSize3220() }}>
                지금 <span>가자지구</span>는
              </h3>
            </div>
            <div className={styles.mainChantSwiperBody}>
              <div className={styles.mainChantYoutube}>
                <iframe
                  width="100%"
                  height="272"
                  src="https://www.youtube.com/embed/j28UkZAxQJQ"
                  title=""
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                  referrerPolicy="strict-origin-when-cross-origin"
                  allowFullScreen
                ></iframe>
              </div>
              <div className={styles.mainChantSwiperBodyItem}>
                <ul>
                  <li>
                    <p style={{ fontSize: calculateFontSize1614() }}>
                      [뉴스] 튀르키예·시리아 지진 발생 1년이 지난 지금, 아직도
                      수많은 어린이와 가족들은 고통받고 있습니다
                    </p>
                  </li>
                  <li>
                    <p style={{ fontSize: calculateFontSize1614() }}>
                      [스토리] 후원자님께 전하는 튀르키예·시리아 지진 6개월 현장
                      이야기
                    </p>
                  </li>
                  <li>
                    <p style={{ fontSize: calculateFontSize1614() }}>
                      [뉴스] 튀르키예·시리아 지진 100일, 끝나지 않은 절망 속에
                      사는 수백만 명의 어린이
                    </p>
                  </li>
                  <li>
                    <p style={{ fontSize: calculateFontSize1614() }}>
                      [보도자료] 김연아 유니세프 국제친선대사 자선 경매 수익금
                      기부 ‘튀르키예·시리아
                    </p>
                  </li>
                </ul>
              </div>
            </div>
          </SwiperSlide>
          <SwiperSlide className="mainChantSwiper chantSlide03">
            <div className="mainChantSwiperTitle">
              <h3 style={{ fontSize: calculateFontSize3220() }}>
                지금 <span>우크라이나</span>는
              </h3>
            </div>
            <div className="mainChantSwiperBody">
              <div className="mainChantYoutube">
                <iframe
                  width="100%"
                  height="272"
                  src="https://www.youtube.com/embed/3vWCyEQnpWo"
                  title=""
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                  referrerPolicy="strict-origin-when-cross-origin"
                  allowFullScreen
                ></iframe>
              </div>
              <div className="mainChantSwiperBodyItem">
                <ul>
                  <li>
                    <p style={{ fontSize: calculateFontSize1614() }}>
                      [뉴스] 튀르키예·시리아 지진 발생 1년이 지난 지금, 아직도
                      수많은 어린이와 가족들은 고통받고 있습니다
                    </p>
