import React, { useState } from 'react';

export default function MainPage() {
  const [selectedButton, setSelectedButton] = useState(null); // 선택된 버튼 상태
  const [priceText, setPriceText] = useState(""); // 가격 텍스트 상태
  const [vaccineText, setVaccineText] = useState(""); // 백신 정보 텍스트 상태

  // 버튼 클릭 핸들러 함수
  const handleButtonClick = (buttonId) => {
    setSelectedButton(buttonId); // 선택된 버튼 업데이트

    // 각 버튼에 맞는 가격과 백신 정보 설정
    switch (buttonId) {
      case 1:
        setPriceText("30,000원");
        setVaccineText("DTP 예방백신 1,200회");
        break;
      case 2:
        setPriceText("50,000원");
        setVaccineText("폴리오 예방백신 2,500회");
        break;
      case 3:
        setPriceText("100,000원");
        setVaccineText("BCG 예방백신 500회");
        break;
      default:
        break;
    }
  };

  return (
    <div className="mainSponsor">
      <div className="mainSponsorSelect">
        <ul>
          <li>
            <button
              onClick={() => handleButtonClick(1)}
              className={selectedButton === 1 ? "active" : ""}
            >
              30,000원
            </button>
          </li>
          <li>
            <button
              onClick={() => handleButtonClick(2)}
              className={selectedButton === 2 ? "active" : ""}
            >
              50,000원
            </button>
          </li>
          <li>
            <button
              onClick={() => handleButtonClick(3)}
              className={selectedButton === 3 ? "active" : ""}
            >
              100,000원
            </button>
          </li>
        </ul>
        <div className="sponsorBodyText">
          <strong>
            월 <span id="price">{priceText}</span>이면, 전 세계 어린이를
            위해 <span id="vaccines">{vaccineText}</span>를 지원할 수
            있습니다.
          </strong>
        </div>
      </div>
    </div>
  );
}
