import React, { useRef, useState } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/autoplay";

import "./css/mainPage.css";

// import required modules
import { Pagination, Navigation, Autoplay } from "swiper/modules";

export default function MainPage() {
  return (
    <>
      <div className="mainCover01wrap">
        <Swiper
          pagination={true}
          /* autoplay={{
            delay: 2000,
            disableOnInteraction: true,
          }} */
          // loop={true}
          navigation={true}
          modules={[Pagination, Navigation, Autoplay]}
          className="mainCover01"
        >
          <SwiperSlide className="mainCoverItem cover01">
            <div>
              <h2>하나일 때 더 빛나는 유니세프 팀 반지</h2>
              <p>유니세프 팀 반지로 당신의 선한 영향력을 보여주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide className="mainCoverItem cover02">
            <div>
              <h2>모든 어린이와 함께하는 유니세프 팀 팔찌</h2>
              <p>가족과 함께 어린이들 이야기에 귀 기울여주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide className="mainCoverItem cover03">
            <div>
              <h2>어린이의 생명을 살리는 '출발점'</h2>
              <p>굶주린 어린이를 위해 출발점을 만들어주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
          <SwiperSlide className="mainCoverItem cover04">
            <div>
              <h2>가자지구 긴급구호</h2>
              <p>참혹한 분쟁으로 위험에 처한 어린이를 구해주세요</p>
              <button type="button">자세히 보기</button>
            </div>
          </SwiperSlide>
        </Swiper>
      </div>
      <div className="mainCover02wrap">
        <Swiper
          pagination={true}
          autoplay={{
            delay: 2000, // 간격을 밀리초 단위로 지정
            disableOnInteraction: true, // 사용자 상호 작용 후에도 자동 재생 유지
          }}
          // loop={true}
          navigation={true}
          modules={[Pagination, Navigation, Autoplay]}
          className="mainCover02"
        >
          <SwiperSlide className="mainCoverItem">
            <img src="/main/mainCoverThum01.png" alt="" />
          </SwiperSlide>
          <SwiperSlide className="mainCoverItem">
            <img src="/main/mainCoverThum02.png" alt="" />
          </SwiperSlide>
          <SwiperSlide className="mainCoverItem">
            <img src="/main/mainCoverThum03.jpg" alt="" />
          </SwiperSlide>
          <SwiperSlide className="mainCoverItem">
            <img src="/main/mainCoverThum04.jpg" alt="" />
          </SwiperSlide>
        </Swiper>
      </div>
    </>
  );
}
